mapper.select:
SELECT memberId, password, name, gender, role, regDate
FROM member
WHERE memberId = #{memberId}

SELECT COUNT(memberId)
FROM member
WHERE memberId = #{memberId}

mapper.update:
UPDATE member
SET gender = #{gender}
WHERE memberId = #{memberId}

mapper.select:
SELECT title, content, sender, sendDate
FROM message
WHERE receiver = #{receiver}
