mapper.update:
update GMEETING
SET title =#{vo.title}, CONTENT = #{vo.content}, LOCATION = #{vo.location},
    COST = #{vo.cost} , CAPACITY = #{vo.capacity}, MEETING_DATE = #{vo.meeting_date}
where memberid = #{memberid}
And gno = #{gno}
AND meetingid = #{meetingid}

mapper.delete:
DELETE FROM GMEETING
WHERE memberid = #{memberid}
  AND gno = #{gno}
  AND meetingid = #{meetingid}
